import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [singleFile, setSingleFile] = useState(null); // Untuk file tunggal
  const [folderFiles, setFolderFiles] = useState(null); // Untuk folder

  // Handle perubahan untuk file tunggal
  const handleSingleFileChange = (e) => {
    setSingleFile(e.target.files[0]);
  };

  // Handle perubahan untuk folder
  const handleFolderChange = (e) => {
    setFolderFiles(e.target.files);
  };

  // Fungsi untuk mengunggah file tunggal
  const handleSingleFileUpload = async () => {
    if (!singleFile) {
      alert("Please select a single file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", singleFile);

    try {
      const response = await fetch("http://localhost:5000/upload/single", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading single file:", error);
      alert("Failed to upload single file.");
    }
  };

  // Fungsi untuk mengunggah folder
  const handleFolderUpload = async () => {
    if (!folderFiles) {
      alert("Please select a folder to upload.");
      return;
    }

    const formData = new FormData();
    for (const file of folderFiles) {
      formData.append("files", file);
    }

    try {
      const response = await fetch("http://localhost:5000/upload/folder", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading folder:", error);
      alert("Failed to upload folder.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>
      <div className="upload-container">
        <div className="upload-section">
          <h3>Upload Single File</h3>
          <input type="file" onChange={handleSingleFileChange} />
          <button onClick={handleSingleFileUpload}>Upload Single File</button>
        </div>
        <div className="upload-section">
          <h3>Upload Folder</h3>
          <input
            type="file"
            multiple
            webkitdirectory=""
            onChange={handleFolderChange}
          />
          <button onClick={handleFolderUpload}>Upload Folder</button>
        </div>
      </div>
    </div>
  );
}
