import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [files, setFiles] = useState([]);
  const [folders, setFolders] = useState([]);

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...uploadedFiles]);
  };

  const handleFolderUpload = (event) => {
    const uploadedFolders = Array.from(event.target.files);
    setFolders((prevFolders) => [...prevFolders, ...uploadedFolders]);
  };

  return (
    <div className="container-homepage">
      <h1>Upload Files and Folders</h1>
      <div className="upload-container">
        <div className="upload-section">
          <label className="upload-label" htmlFor="file-upload">
            Upload Files:
          </label>
          <input
            id="file-upload"
            type="file"
            multiple
            onChange={handleFileUpload}
            className="upload-input"
          />
        </div>

        <div className="upload-section">
          <label className="upload-label" htmlFor="folder-upload">
            Upload Folders:
          </label>
          <input
            id="folder-upload"
            type="file"
            multiple
            webkitdirectory="true"
            onChange={handleFolderUpload}
            className="upload-input"
          />
        </div>
      </div>

      <div className="uploaded-files">
        <h2>Uploaded Files:</h2>
        <ul>
          {files.map((file, index) => (
            <li key={index}>{file.name}</li>
          ))}
        </ul>
      </div>

      <div className="uploaded-folders">
        <h2>Uploaded Folders:</h2>
        <ul>
          {folders.map((folder, index) => (
            <li key={index}>{folder.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
