import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [isUploading, setIsUploading] = useState(false); // Status upload

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
      setFileUploaded(true);
      setFolderUploaded(false);
      setUploadStatus(null);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let fileList = [];
    for (let i = 0; i < files.length; i++) {
      if (files[i].name.endsWith(".js") || files[i].name.endsWith(".py")) {
        fileList.push(files[i]);
      }
    }
    if (fileList.length > 0) {
      setSelectedFiles(fileList);
      setFolderUploaded(true);
      setFileUploaded(false);
      setFolderContents(fileList.map(file => file.name));
      setUploadStatus(null);
    } else {
      alert("Tidak ada file dengan ekstensi .js atau .py yang ditemukan.");
      setFolderUploaded(false);
    }
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      alert("Tidak ada file yang dipilih.");
      return;
    }

    if (isUploading) return; // Cegah multiple request

    setIsUploading(true);
    setUploadStatus(null);

    const formData = new FormData();
    selectedFiles.forEach(file => formData.append("files", file));

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const contentType = response.headers.get("content-type");

      if (contentType && contentType.includes("application/json")) {
        const result = await response.json();
        console.log("Response dari backend:", result);
        
        // Hindari infinite loop dengan hanya update jika ada perubahan
        setUploadStatus(prevStatus => {
          if (JSON.stringify(prevStatus) !== JSON.stringify(result)) {
            return result;
          }
          return prevStatus;
        });

        if (response.ok) {
          alert(`Upload berhasil! Total ukuran: ${result.total_size_MB} MB`);
        } else {
          alert(`Upload gagal! ${result.error}`);
        }
      } else {
        const text = await response.text();
        console.error("Server mengembalikan respons yang bukan JSON:", text);
        alert("Terjadi kesalahan di server, respons bukan JSON. Cek console.");
      }
    } catch (error) {
      console.error("Upload gagal:", error);
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input type="file" webkitdirectory="" directory="" multiple onChange={handleFolderUpload} disabled={fileUploaded} />
            </label>
          </div>
        </div>
        <button className="upload-button" onClick={handleUploadToBackend} disabled={isUploading}>
          {isUploading ? "Uploading..." : "Upload"}
        </button>
      </div>

      {isUploading && (
        <div className="upload-status">
          <h3>Uploading...</h3>
        </div>
      )}

      {selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul>
            {selectedFiles.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Menampilkan Status Upload Jika Berhasil */}
      {uploadStatus?.saved_files?.length > 0 && (
        <div className="upload-status success">
          <h3>Upload Berhasil!</h3>
          <ul>
            {uploadStatus.saved_files.map((file, index) => (
              <li key={index}>
                {file.filename} - {file.size_MB} MB ({file.size_GB} GB)
              </li>
            ))}
          </ul>
          <p><strong>Total Ukuran:</strong> {uploadStatus.total_size_MB} MB ({uploadStatus.total_size_GB} GB)</p>
        </div>
      )}

      {/* Menampilkan Error Jika File Terlalu Besar */}
      {uploadStatus?.error && (
        <div className="upload-status error">
          <h3>Kesalahan:</h3>
          <p>{uploadStatus.error}</p>
          {uploadStatus.rejected_files?.length > 0 && (
            <div>
              <h4>File yang Gagal Diupload:</h4>
              <ul>
                {uploadStatus.rejected_files.map((file, index) => (
                  <li key={index}>
                    {file.filename} - {file.size_MB} MB ({file.size_GB} GB)
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
