import React, { useState } from 'react';
import './Homepage.css';

function Homepage() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [filteredResult, setFilteredResult] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFiles(Array.from(e.target.files)); // Handle multiple files
  };

  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      alert('Please select a folder or files.');
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach((file) => formData.append('files', file)); // Add multiple files

    try {
      const response = await fetch('http://127.0.0.1:5050/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const error = await response.json();
        alert(error.message);
        return;
      }

      const data = await response.json();
      setFilteredResult(data);
    } catch (err) {
      console.error('Error uploading files:', err);
    }
  };

  return (
    <div className="homepage">
      <h1>Upload Source Code</h1>
      <input
        type="file"
        webkitdirectory="true"
        mozdirectory="true"
        multiple
        onChange={handleFileChange}
      />
      <button onClick={handleUpload}>Upload</button>

      {filteredResult && (
        <div className="result">
          <h2>Filtered Files:</h2>
          <div className="container">
            <div className="js-container">
              <h3>JavaScript Files</h3>
              <ul>
                {filteredResult.jsFiles.map((file, index) => (
                  <li key={index}>{file}</li>
                ))}
              </ul>
            </div>
            <div className="py-container">
              <h3>Python Files</h3>
              <ul>
                {filteredResult.pyFiles.map((file, index) => (
                  <li key={index}>{file}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Homepage;
