import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState({
    uploaded: false,
    analyzed: false,
    message: ""
  });
  const [isUploading, setIsUploading] = useState(false);
  const [progress, setProgress] = useState({});

  // Increased chunk size to 10MB for faster uploads
  const CHUNK_SIZE = 1024 * 1024 * 10; 
  const MAX_CONCURRENT_UPLOADS = 3;

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
      setFileUploaded(true);
      setFolderUploaded(false);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let fileList = [];
    for (let i = 0; i < files.length; i++) {
      if (files[i].name.endsWith(".js") || files[i].name.endsWith(".py")) {
        fileList.push(files[i]);
      }
    }
    if (fileList.length > 0) {
      setSelectedFiles(fileList);
      setFolderUploaded(true);
      setFileUploaded(false);
    } else {
      alert("Tidak ada file dengan ekstensi .js atau .py yang ditemukan.");
      setFolderUploaded(false);
    }
  };

  const uploadChunk = async (file, start, chunk, sessionId) => {
    const formData = new FormData();
    formData.append("file", chunk);
    formData.append("sessionId", sessionId);
    formData.append("start", start);
    formData.append("total_size", file.size);
    formData.append("filename", file.name);

    const response = await fetch("http://localhost:5000/upload-chunk", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error(`Upload chunk failed: ${response.statusText}`);
    }
    
    return await response.json();
  };

  const uploadFile = async (file, sessionId) => {
    const chunks = Math.ceil(file.size / CHUNK_SIZE);
    setProgress(prev => ({ ...prev, [file.name]: 0 }));

    for (let i = 0; i < chunks; i++) {
      const start = i * CHUNK_SIZE;
      const chunk = file.slice(start, start + CHUNK_SIZE);
      await uploadChunk(file, start, chunk, sessionId);
      
      setProgress(prev => ({
        ...prev,
        [file.name]: Math.round(((i + 1) / chunks) * 100)
      }));
    }
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      alert("Tidak ada file yang dipilih.");
      return;
    }

    setIsUploading(true);
    const sessionId = Date.now().toString();

    try {
      // Process files in batches for parallel upload
      for (let i = 0; i < selectedFiles.length; i += MAX_CONCURRENT_UPLOADS) {
        const batch = selectedFiles.slice(i, i + MAX_CONCURRENT_UPLOADS);
        await Promise.all(batch.map(file => uploadFile(file, sessionId)));
      }

      const finalizeResponse = await fetch("http://localhost:5000/finalize-upload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          files: selectedFiles.map(f => ({ 
            filename: f.name, 
            size: f.size 
          }))
        }),
      });

      if (!finalizeResponse.ok) {
        throw new Error("Failed to finalize upload");
      }

      const result = await finalizeResponse.json();
      setUploadStatus({
        uploaded: true,
        analyzed: false,
        message: result.message
      });

      alert("Upload berhasil!");
    } catch (error) {
      console.error("Upload error:", error);
      setUploadStatus({
        uploaded: false,
        analyzed: false,
        message: error.message
      });
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
      setProgress({});
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input type="file" webkitdirectory="" directory="" multiple onChange={handleFolderUpload} disabled={fileUploaded} />
            </label>
          </div>
        </div>
        <button className="upload-button" onClick={handleUploadToBackend} disabled={isUploading}>
          {isUploading ? "Uploading..." : "Upload"}
        </button>
      </div>

      {selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul>
            {selectedFiles.map((file, index) => (
              <li key={index}>
                {file.name}
                {progress[file.name] !== undefined && (
                  <span className="progress"> - {progress[file.name]}%</span>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
