import React, { useState } from 'react';
import './Homepage.css';

export default function Homepage() {
    const [message, setMessage] = useState('');

    // Function to handle file upload
    const handleFileUpload = (e) => {
        const files = e.target.files;
        const validFiles = [];
        
        // Loop through selected files to validate them
        for (let i = 0; i < files.length; i++) {
            const file = files[i];
            // Simple check to simulate file extension and size validation
            const fileExtension = file.name.split('.').pop().toLowerCase();
            if (['js', 'python'].includes(fileExtension) && file.size <= 1 * 1024 * 1024 * 1024) {
                validFiles.push(file);
            }
        }

        // Set message based on file validation result
        if (validFiles.length > 0) {
            setMessage(`Successfully selected ${validFiles.length} valid files.`);
        } else {
            setMessage('Please select only .js or .python files, and they must be under 1GB.');
        }
    };

    return (
        <div className="homepage-container">
            <div className="upload-box">
                <h1>Welcome to the Homepage</h1>
                <p>Select files to upload</p>
                <input
                    type="file"
                    multiple
                    onChange={handleFileUpload}
                    style={{ marginBottom: '20px' }}
                />
                <div className="message-box">
                    <p>{message}</p>
                </div>
            </div>
        </div>
    );
}
