import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]); // Menyimpan daftar file dalam folder
  const [selectedFiles, setSelectedFiles] = useState([]); // Menyimpan daftar file yang dipilih sebelum upload

  // Fungsi untuk memvalidasi ekstensi file
  const isValidFile = (file) => {
    const allowedExtensions = [".js", ".py"];
    return allowedExtensions.some((ext) => file.name.toLowerCase().endsWith(ext));
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (!file) return;

    if (!isValidFile(file)) {
      alert("File tidak didukung! Hanya file .js dan .py yang diperbolehkan.");
      return;
    }

    if (file.size > 1024 * 1024 * 1024) {
      alert("File terlalu besar! Maksimum 1GB.");
      return;
    }

    setFileUploaded(true);
    setFolderUploaded(false);
    setFolderContents([]); // Reset tampilan folder jika file dipilih
    setSelectedFiles([file.name]); // Simpan file yang dipilih
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let isTooLarge = false;
    let fileList = [];

    for (let i = 0; i < files.length; i++) {
      if (!isValidFile(files[i])) {
        alert(`File ${files[i].name} tidak didukung! Hanya file .js dan .py yang diperbolehkan.`);
        return;
      }

      if (files[i].size > 1024 * 1024 * 1024) {
        isTooLarge = true;
        break;
      }
      fileList.push(files[i].webkitRelativePath); // Ambil path relatif dari file dalam folder
    }

    if (isTooLarge) {
      alert("Salah satu file dalam folder terlalu besar! Maksimum 1GB per file.");
      return;
    }

    if (files.length > 0) {
      setFolderUploaded(true);
      setFileUploaded(false);
      setFolderContents(fileList);
      setSelectedFiles(fileList); // Simpan daftar file yang dipilih
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-wrapper">
        {/* Container Upload */}
        <div className="upload-container">
          <h2>Upload File or Folder</h2>
          <p className="upload-limit">Maksimum ukuran file: 1GB</p>
          <div className="upload-options">
            <div className="upload-box">
              <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
                Upload File
                <input type="file" onChange={handleFileUpload} disabled={folderUploaded} />
              </label>
            </div>
            <div className="upload-box">
              <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
                Upload Folder
                <input type="file" webkitdirectory="" directory="" onChange={handleFolderUpload} disabled={fileUploaded} />
              </label>
            </div>
          </div>
          <button className="upload-button">Upload</button>
        </div>

        {/* Container Baru untuk Menampilkan Isi File yang Dipilih */}
        {(fileUploaded || folderUploaded || selectedFiles.length > 0) && (
          <div className="folder-content-container">
            <h3>Isi File yang Dipilih:</h3>
            <ul>
              {selectedFiles.map((file, index) => (
                <li key={index}>{file}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
