import React, { useState } from "react";
import "./HomePage.css";

export default function HomePage() {
  const [file, setFile] = useState(null);
  const [folder, setFolder] = useState(null);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.size > 1024 * 1024 * 1024) { // 1GB limit
      setError("File size exceeds 1GB. Please upload a smaller file.");
      setFile(null);
    } else {
      setFile(selectedFile);
      setError("");
    }
  };

  const handleFolderChange = (e) => {
    const selectedFolder = e.target.files;
    if (selectedFolder.length > 0) {
      setFolder(selectedFolder);
      setError("");
    } else {
      setFolder(null);
      setError("No folder selected. Please select a folder.");
    }
  };

  const handleUpload = () => {
    if (file || folder) {
      if (file) {
        console.log("Uploading file:", file);
      }
      if (folder) {
        console.log("Uploading folder:", folder);
      }
      alert("Upload successful!");
    } else {
      alert("Please select a file or folder first.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <div className="text-center mb-6">
              <h1 className="title">Upload Your File or Folder</h1>
              <p className="description">Maximum file size: 1GB</p>
            </div>
            <div className="form-container">
              <input
                type="file"
                onChange={handleFileChange}
                className="file-input"
              />
              <p className="description">Or select a folder:</p>
              <input
                type="file"
                webkitdirectory="true"
                mozdirectory="true"
                directory="true"
                onChange={handleFolderChange}
                className="file-input"
              />
              {error && <p className="error-message">{error}</p>}
              <button
                onClick={handleUpload}
                disabled={!file && !folder}
                className="upload-button"
              >
                Upload
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
