import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [error, setError] = useState("");

  const handleDrop = async (e) => {
    e.preventDefault();
    const items = e.dataTransfer.items;
    const files = [];

    for (const item of items) {
      if (item.kind === "file") {
        const fileEntry = item.webkitGetAsEntry();

        if (fileEntry.isFile) {
          const file = item.getAsFile();
          files.push(file);
        } else if (fileEntry.isDirectory) {
          await readDirectory(fileEntry, files);
        }
      }
    }

    const totalSize = files.reduce((sum, file) => sum + file.size, 0);
    if (totalSize > 1024 * 1024 * 1024) {
      setError("Selected files or folder exceed 1GB. Please upload smaller items.");
      setSelectedItems([]);
    } else {
      setError("");
      setSelectedItems(files);
    }
  };

  const readDirectory = async (directoryEntry, files) => {
    const reader = directoryEntry.createReader();
    let entries = await new Promise((resolve) => reader.readEntries(resolve));

    for (const entry of entries) {
      if (entry.isFile) {
        const file = await new Promise((resolve) => entry.file(resolve));
        files.push(file);
      } else if (entry.isDirectory) {
        await readDirectory(entry, files);
      }
    }
  };

  const handleUpload = async () => {
    if (selectedItems.length === 0) {
      alert("Please select a file or folder.");
      return;
    }

    console.log("Uploading:");
    selectedItems.forEach((file) => console.log("File:", file.name));
    alert("Upload successful!");
  };

  return (
    <div
      className="container-homepage"
      onDragOver={(e) => e.preventDefault()}
      onDrop={handleDrop}
    >
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <h1 className="title">Upload Your File or Folder</h1>
            <p className="description">Drag and drop files or folders here. Maximum size: 1GB</p>
            <div className="drop-zone">Drag & Drop Here</div>
            {error && <p className="error-message">{error}</p>}
            <button
              onClick={handleUpload}
              disabled={selectedItems.length === 0}
              className="upload-button"
            >
              Upload
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
