import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [uploadedFolders, setUploadedFolders] = useState([]);

  const handleFileUpload = (event) => {
    const files = Array.from(event.target.files);
    const filePaths = files.map(file => file.name);
    setUploadedFiles(filePaths);
  };

  const handleFolderUpload = (event) => {
    const files = Array.from(event.target.files);
    const folderPaths = files.map(file => file.webkitRelativePath || file.name);
    setUploadedFolders(folderPaths);
  };

  return (
    <div className="container-homepage">
      {/* Navbar */}
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>

      {/* File Upload Section */}
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <div className="upload-buttons">
          <label className="custom-file-upload">
            Choose File
            <input type="file" className="file-input" multiple onChange={handleFileUpload} />
          </label>
          <label className="custom-folder-upload">
            Choose Folder
            <input type="file" className="file-input" webkitdirectory directory multiple onChange={handleFolderUpload} />
          </label>
        </div>

        {/* Display Uploaded Files */}
        <div className="uploaded-files">
          <h3>Uploaded Files:</h3>
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>{file}</li>
            ))}
          </ul>
        </div>

        {/* Display Uploaded Folders */}
        <div className="uploaded-folders">
          <h3>Uploaded Folders:</h3>
          <ul>
            {uploadedFolders.map((file, index) => (
              <li key={index}>{file}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
