import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedFiles, setSelectedFiles] = useState([]);

  // Open File Picker to Select Files from Folder
  const handleFileSelection = async () => {
    try {
      const filesHandles = await window.showOpenFilePicker({
        multiple: true, // Allows multiple files selection
      });

      const files = await Promise.all(
        filesHandles.map(async (fileHandle) => await fileHandle.getFile())
      );

      setSelectedFiles(files);
      alert(`Selected ${files.length} files from folder.`);
    } catch (error) {
      console.error("File selection failed:", error);
    }
  };

  // Upload selected files to backend
  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      alert("Please select files before uploading.");
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach((file) => formData.append("files", file));

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>
      <div className="upload-container">
        <h3>Upload Folder Contents</h3>
        <button onClick={handleFileSelection}>Choose Folder</button>
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
}
