import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [files, setFiles] = useState([]);

  const handleFileUpload = (e) => {
    const uploadedFiles = Array.from(e.target.files);
    setFiles(uploadedFiles);
  };

  const handleChooseFile = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.multiple = true;
    input.setAttribute("webkitdirectory", ""); // Allow folder selection
    input.setAttribute("directory", "");
    input.onchange = (event) => handleFileUpload(event);
    input.click();
  };

  const handleUpload = () => {
    if (files.length === 0) {
      alert("No files selected!");
      return;
    }
    alert("Files uploaded successfully!");
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-content">
          <h1 className="navbar-title">HauntAI</h1>
        </div>
      </nav>
      <div className="upload-card">
        <div className="upload-section">
          <h3>Upload Files or Folder</h3>
          <button className="file-input" onClick={handleChooseFile}>
            Choose File or Folder
          </button>
          {files.length > 0 && (
            <div className="file-list">
              <h4>Selected Files:</h4>
              <ul>
                {files.map((file, index) => (
                  <li key={index}>{file.name}</li>
                ))}
              </ul>
            </div>
          )}
          <p>Max file size: 1GB</p>
          <button className="upload-button" onClick={handleUpload}>Upload</button>
        </div>
      </div>
    </div>
  );
}
