import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folders, setFolders] = useState([]); // Menyimpan seluruh isi folder
  const [acceptedFiles, setAcceptedFiles] = useState([]); // Hanya file .js & .py

  // Fungsi untuk memvalidasi ekstensi file
  const isValidFile = (file) => {
    const allowedExtensions = [".js", ".py"];
    return allowedExtensions.some((ext) => file.toLowerCase().endsWith(ext));
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    if (!file) return;

    if (!isValidFile(file.name)) {
      alert("File tidak didukung! Hanya file .js dan .py yang diperbolehkan.");
      return;
    }

    if (file.size > 1024 * 1024 * 1024) {
      alert("File terlalu besar! Maksimum 1GB.");
      return;
    }

    setFileUploaded(true);
    setFolders([]); // Reset daftar folder jika memilih file tunggal
    setAcceptedFiles([file.name]); // Simpan file yang diterima
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let newFolders = {};
    let acceptedFilesList = [];

    for (let i = 0; i < files.length; i++) {
      const folderName = files[i].webkitRelativePath.split("/")[0]; // Ambil nama folder

      // Simpan semua file dalam folder
      if (!newFolders[folderName]) {
        newFolders[folderName] = [];
      }
      newFolders[folderName].push(files[i].webkitRelativePath);

      // Hanya simpan file .js & .py di daftar diterima
      if (isValidFile(files[i].name)) {
        acceptedFilesList.push(files[i].webkitRelativePath);
      }
    }

    // Konversi objek ke array [{name: "folder1", files: ["file1.js", "file2.py"]}]
    const folderArray = Object.keys(newFolders).map((name) => ({
      name,
      files: newFolders[name],
    }));

    setFolders(folderArray);
    setAcceptedFiles(acceptedFilesList);
  };

  const handleUpload = () => {
    alert("File sedang diproses ke backend!");
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>

      <div className="upload-wrapper">
        {/* Container Upload */}
        <div className="upload-container">
          <h2>Upload File or Folder</h2>
          <p className="upload-limit">Maksimum ukuran file: 1GB</p>
          <div className="upload-options">
            <div className="upload-box">
              <label className="upload-label">
                Upload File
                <input type="file" onChange={handleFileUpload} />
              </label>
            </div>
            <div className="upload-box">
              <label className="upload-label">
                Upload Folder
                <input type="file" webkitdirectory="" directory="" onChange={handleFolderUpload} />
              </label>
            </div>
          </div>
          <button className="upload-button" onClick={handleUpload}>Upload ke Server</button>
        </div>
      </div>

      {/* Wrapper untuk daftar folder & file yang diterima */}
      <div className="content-wrapper">
        {/* Container untuk Semua Folder */}
        {folders.length > 0 && (
          <div className="folder-list">
            {folders.map((folder, index) => (
              <div className="folder-content-container" key={index}>
                <h3>Folder: {folder.name}</h3>
                <ul>
                  {folder.files.map((file, idx) => (
                    <li key={idx}>{file}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}

        {/* Container untuk File yang Diterima */}
        {acceptedFiles.length > 0 && (
          <div className="accepted-files-container">
            <h3>File yang Diterima (.js & .py):</h3>
            <ul>
              {acceptedFiles.map((file, index) => (
                <li key={index}>{file}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
