import React, { useState } from 'react';
import './Homepage.css';

export default function Homepage() {
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    // Maksimal ukuran file (1 GB) dalam byte
    const MAX_FILE_SIZE = 1 * 1024 * 1024 * 1024; // 1 GB

    // Handle file selection
    const handleFileChange = (event) => {
        const files = event.target.files;
        if (files) {
            let validFiles = [];
            let error = false;

            // Periksa ukuran file dan filter yang valid
            for (let i = 0; i < files.length; i++) {
                const file = files[i];
                if (file.size > MAX_FILE_SIZE) {
                    setErrorMessage('One or more files are too large! Maximum file size is 1 GB.');
                    error = true;
                    break;
                } else {
                    validFiles.push(file);
                }
            }

            if (!error) {
                setErrorMessage('');
                setSelectedFiles(validFiles);
            }
        }
    };

    // Handle file upload (placeholder logic)
    const handleUpload = () => {
        if (selectedFiles.length > 0) {
            selectedFiles.forEach(file => {
                alert(`File "${file.name}" uploaded successfully!`);
            });
            // Implement actual file upload logic here
        } else {
            alert('Please select files or folder first.');
        }
    };

    return (
        <div className='container'>
            <h1>Upload Files or Folder</h1>
            <div className="upload-section">
                <input 
                    type="file" 
                    onChange={handleFileChange} 
                    className="file-input" 
                    multiple
                    webkitdirectory="true"
                />
                <button 
                    onClick={handleUpload} 
                    className="upload-button"
                >
                    Upload
                </button>
            </div>
            {errorMessage && <p className="error-message">{errorMessage}</p>}
            {selectedFiles.length > 0 && !errorMessage && (
                <div className="file-info">
                    <h3>Selected Files:</h3>
                    <ul>
                        {selectedFiles.map(file => (
                            <li key={file.name}>
                                <p><strong>{file.name}</strong></p>
                                <p>Size: {(file.size / 1024 / 1024).toFixed(2)} MB</p>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}
