import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [uploadedFiles, setUploadedFiles] = useState([]);

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    const filePaths = Array.from(files).map(file => file.webkitRelativePath);
    setUploadedFiles(filePaths); // Menyimpan path file untuk ditampilkan di UI
  };

  return (
    <div className="container-homepage">
      {/* Navbar */}
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>

      {/* File Upload Section */}
      <div className="upload-container">
        <h2>Upload Your File or Folder</h2>
        <form>
          {/* File Upload */}
          <input type="file" id="fileUpload" className="file-input" />

          {/* Folder Upload */}
          <input 
            type="file" 
            id="folderUpload" 
            className="file-input" 
            webkitdirectory 
            directory 
            multiple 
            onChange={handleFolderUpload} 
          />
          
          <button type="submit" className="upload-button">Upload</button>
        </form>

        {/* Display Uploaded Files */}
        <div className="uploaded-files">
          <h3>Uploaded Files:</h3>
          <ul>
            {uploadedFiles.map((file, index) => (
              <li key={index}>{file}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
