import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState({
    uploaded: false,
    analyzed: false,
    message: "",
    analysisResults: [],
  });
  const [isUploading, setIsUploading] = useState(false);
  const [aiResponse, setAiResponse] = useState("");

  const CHUNK_SIZE = 1024 * 1024 * 500; // 500MB

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
    }
  };

  const uploadChunk = async (file, start, chunk, sessionId) => {
    const formData = new FormData();
    formData.append("file", chunk);
    formData.append("sessionId", sessionId);
    formData.append("start", start);
    formData.append("total_size", file.size);
    formData.append("filename", file.name);

    const response = await fetch("http://localhost:5000/upload-chunk", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error(`Upload chunk failed: ${response.statusText}`);
    }
    return await response.json();
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      alert("Tidak ada file yang dipilih.");
      return;
    }
    setIsUploading(true);
    const sessionId = Date.now().toString();

    try {
      for (const file of selectedFiles) {
        let start = 0;
        while (start < file.size) {
          const chunk = file.slice(start, start + CHUNK_SIZE);
          await uploadChunk(file, start, chunk, sessionId);
          start += CHUNK_SIZE;
        }
      }

      const finalizeResponse = await fetch("http://localhost:5000/finalize-upload", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sessionId }),
      });

      const result = await finalizeResponse.json();
      setUploadStatus({ uploaded: true, analyzed: true, analysisResults: result.saved_files });
      await analyzeWithAI(result.saved_files);
    } catch (error) {
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
    }
  };

  const analyzeWithAI = async (files) => {
    try {
      const response = await fetch("https://generativelanguage.googleapis.com/v1/models/gemini-pro:generateText?key=AIzaSyCFmoUUGoTFMyhgkS8eWgSrTr0VC-HSoQg", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ prompt: `Analyze the following files: ${files.join(", ")}. Provide a detailed reason for your analysis.` }),
      });
      const result = await response.json();
      setAiResponse(result.choices[0].text);
    } catch (error) {
      console.error("Error analyzing with AI:", error);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File</h2>
        <input type="file" multiple onChange={handleFileUpload} />
        <button onClick={handleUploadToBackend} disabled={isUploading}>
          {isUploading ? "Uploading..." : "Upload"}
        </button>
      </div>

      {uploadStatus.analyzed && (
        <div className="analysis-container">
          <h3>Hasil Analisis</h3>
          <pre>{aiResponse}</pre>
        </div>
      )}
    </div>
  );
}
