import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [error, setError] = useState("");

  const handleFileChange = async (e) => {
    const files = Array.from(e.target.files);
    const items = [];

    for (const file of files) {
      if (file.webkitRelativePath) {
        // File dari folder
        items.push(file);
      } else {
        // File biasa
        items.push(file);
      }
    }

    const totalSize = items.reduce((sum, file) => sum + file.size, 0);
    if (totalSize > 1024 * 1024 * 1024) { // Validasi 1GB
      setError("Selected files or folder exceed 1GB. Please upload smaller items.");
      setSelectedItems([]);
    } else {
      setError("");
      setSelectedItems(items);
    }
  };

  const handleUpload = async () => {
    if (selectedItems.length === 0) {
      alert("Please select a file or folder.");
      return;
    }

    console.log("Uploading:");
    selectedItems.forEach((file) => console.log("File:", file.name));
    alert("Upload successful!");
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <h1 className="title">Upload Your File or Folder</h1>
            <p className="description">You can upload single files or folders. Maximum size: 1GB</p>
            <input
              type="file"
              onChange={handleFileChange}
              className="file-input"
              multiple
              webkitdirectory="" // Mendukung folder
            />
            {error && <p className="error-message">{error}</p>}
            <button
              onClick={handleUpload}
              disabled={selectedItems.length === 0}
              className="upload-button"
            >
              Upload
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
