import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedFiles, setSelectedFiles] = useState([]); // Store selected files

  // Handle file/folder selection
  const handleFileChange = (e) => {
    const files = Array.from(e.target.files); // Convert FileList to array
    setSelectedFiles(files); // Store files in state
  };

  // Upload files
  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      alert("Please select files or a folder to upload.");
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach((file) => {
      formData.append("files", file);
    });

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>
      <div className="upload-container">
        <h3>Upload File or Folder</h3>
        <input
          type="file"
          multiple
          webkitdirectory="" // Allows folder selection
          onChange={handleFileChange}
        />
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
}
