import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [error, setError] = useState("");

  const handleSelectionChange = (e) => {
    const selectedFiles = e.target.files;
    if (selectedFiles.length > 0) {
      const totalSize = Array.from(selectedFiles).reduce((sum, file) => sum + file.size, 0);

      if (totalSize > 1024 * 1024 * 1024) { // 1GB limit
        setError("Selected file(s) or folder exceeds 1GB. Please upload a smaller item.");
        setSelectedItem(null);
      } else {
        setSelectedItem(selectedFiles);
        setError("");
      }
    } else {
      setSelectedItem(null);
      setError("No file or folder selected. Please select one.");
    }
  };

  const handleUpload = () => {
    if (selectedItem) {
      console.log("Uploading:", selectedItem);
      Array.from(selectedItem).forEach(file => console.log("File:", file.name));
      alert("Upload successful!");
    } else {
      alert("Please select a file or folder first.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <div className="text-center mb-6">
              <h1 className="title">Upload Your File or Folder</h1>
              <p className="description">Maximum size: 1GB</p>
            </div>
            <div className="form-container">
              <label className="upload-label">
                <input
                  type="file"
                  onChange={handleSelectionChange}
                  className="file-input"
                  multiple
                  webkitdirectory="true"
                />
                Upload
              </label>
              {error && <p className="error-message">{error}</p>}
              <button
                onClick={handleUpload}
                disabled={!selectedItem}
                className="upload-button"
              >
                Upload
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
