import React, { useState, useEffect } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState(null);

  useEffect(() => {
    if (uploadStatus) {
      console.log("Upload Status Updated:", uploadStatus);
    }
  }, [uploadStatus]); // ✅ Ini mencegah loop tak terbatas

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      alert("Tidak ada file yang dipilih.");
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach(file => formData.append("files", file));

    try {
      console.log("Mengirim file ke backend:", selectedFiles);

      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const result = await response.json();
      console.log("Response dari backend:", result);

      setUploadStatus(result); // ✅ Sekarang di-handle di `useEffect`
      
      if (result.saved_files && result.saved_files.length > 0) {
        alert(`Upload berhasil! ${result.saved_files.length} file disimpan.\nLokasi: ${result.save_location}`);
      } else {
        alert("Upload gagal: Tidak ada file yang disimpan.");
      }
    } catch (error) {
      console.error("Upload gagal:", error);
      alert(`Upload gagal: ${error.message}`);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input type="file" multiple onChange={(e) => setSelectedFiles(Array.from(e.target.files))} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input type="file" webkitdirectory="" directory="" multiple onChange={(e) => setSelectedFiles(Array.from(e.target.files))} disabled={fileUploaded} />
            </label>
          </div>
        </div>
        <button className="upload-button" onClick={handleUploadToBackend}>Upload</button>
      </div>

      {/* Container untuk Menampilkan Isi File yang Akan Diupload */}
      {selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul>
            {selectedFiles.map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Menampilkan Status Upload */}
      {uploadStatus && (
        <div className="upload-status">
          <h3>Status Upload:</h3>
          <p>{uploadStatus.message}</p>
        </div>
      )}
    </div>
  );
}
