import React, { useState } from 'react';
import axios from 'axios';
import './Homepage.css';  // Import file CSS

export default function Homepage() {
    const [jsFiles, setJsFiles] = useState([]);
    const [pythonFiles, setPythonFiles] = useState([]);
    const [message, setMessage] = useState('');

    // Maksimum ukuran file 1GB
    const MAX_FILE_SIZE = 1 * 1024 * 1024 * 1024; // 1GB dalam byte

    // Fungsi untuk memeriksa ekstensi file yang diterima
    const allowedExtensions = ['js', 'python'];

    const handleFileChange = (e) => {
        const selectedFiles = e.target.files;
        const jsFilesList = [];
        const pythonFilesList = [];

        // Memeriksa file yang dipilih
        for (let i = 0; i < selectedFiles.length; i++) {
            const file = selectedFiles[i];
            const fileExtension = file.name.split('.').pop().toLowerCase();
            const fileSize = file.size;

            // Memeriksa apakah ekstensi file valid dan ukuran file tidak melebihi 1GB
            if (allowedExtensions.includes(fileExtension) && fileSize <= MAX_FILE_SIZE) {
                if (fileExtension === 'js') {
                    jsFilesList.push(file);
                } else if (fileExtension === 'python') {
                    pythonFilesList.push(file);
                }
            }
        }

        // Update state sesuai hasil seleksi file
        if (jsFilesList.length > 0 || pythonFilesList.length > 0) {
            setJsFiles(jsFilesList);
            setPythonFiles(pythonFilesList);
            setMessage('Files successfully selected.');
        } else {
            setMessage('No valid files selected. Please select only .js or .python files under 1GB.');
        }
    };

    const handleUpload = async () => {
        if (jsFiles.length === 0 && pythonFiles.length === 0) {
            alert('Please select valid files to upload.');
            return;
        }

        const formData = new FormData();
        jsFiles.forEach(file => {
            formData.append('file', file);
        });
        pythonFiles.forEach(file => {
            formData.append('file', file);
        });

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            console.log('Files uploaded:', response.data);
            alert(response.data.message);
        } catch (error) {
            console.error('Error uploading files:', error);
            alert("File upload failed!");
        }
    };

    return (
        <div className="upload-container">
            <h1>Homepage - Upload Files</h1>
            <input 
                type="file" 
                multiple 
                onChange={handleFileChange} 
                webkitdirectory="true" 
                directory="true"
                style={{ marginBottom: '20px' }}
            />
            <div className="message">
                <p>{message}</p>
            </div>
            <button onClick={handleUpload}>Upload Files</button>

            {jsFiles.length > 0 && (
                <div>
                    <h3>JavaScript Files:</h3>
                    <div className="scrollable-box">
                        <ul>
                            {jsFiles.map((file, index) => (
                                <li key={index}>{file.name} ({(file.size / (1024 * 1024)).toFixed(2)} MB)</li>
                            ))}
                        </ul>
                    </div>
                </div>
            )}

            {pythonFiles.length > 0 && (
                <div>
                    <h3>Python Files:</h3>
                    <div className="scrollable-box">
                        <ul>
                            {pythonFiles.map((file, index) => (
                                <li key={index}>{file.name} ({(file.size / (1024 * 1024)).toFixed(2)} MB)</li>
                            ))}
                        </ul>
                    </div>
                </div>
            )}
        </div>
    );
}
