import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedFiles, setSelectedFiles] = useState([]);

  // Handle folder selection using File System API
  const handleFolderSelection = async () => {
    try {
      const handle = await window.showDirectoryPicker(); // Open folder picker
      const files = [];

      for await (const entry of handle.values()) {
        if (entry.kind === "file") {
          const file = await entry.getFile();
          files.push(file);
        }
      }

      setSelectedFiles(files);
      alert(`Folder selected with ${files.length} files`);
    } catch (error) {
      console.error("Folder selection failed:", error);
    }
  };

  // Upload selected files to backend
  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      alert("Please select a folder before uploading.");
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach((file) => formData.append("files", file));

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>
      <div className="upload-container">
        <h3>Upload Folder</h3>
        <button onClick={handleFolderSelection}>Choose Folder</button>
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
}
