import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState({
    uploaded: false,
    analyzed: false,
    message: "",
    analysisResults: [], // Menyimpan hasil analisis
  });
  const [isUploading, setIsUploading] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false); // New state to track analysis status
  const [fileProgress, setFileProgress] = useState({}); // Track progress per file
  const [analysisDetails, setAnalysisDetails] = useState({}); // Store detailed analysis results

  const CHUNK_SIZE = 1024 * 1024 * 500; //500MB

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
      setFileUploaded(true);
      setFolderUploaded(false);
      console.log(`Selected ${files.length} files for upload`);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let fileList = [];
    for (let i = 0; i < files.length; i++) {
      if (files[i].name.endsWith(".js") || files[i].name.endsWith(".py")) {
        fileList.push(files[i]);
      }
    }
    if (fileList.length > 0) {
      setSelectedFiles(fileList);
      setFolderUploaded(true);
      setFileUploaded(false);
      setFolderContents(fileList.map(file => file.name));
      console.log(`Selected ${fileList.length} files from folder`);
    } else {
      console.warn("No .js or .py files found in folder");
      alert("Tidak ada file dengan ekstensi .js atau .py yang ditemukan.");
      setFolderUploaded(false);
    }
  };

  const uploadChunk = async (file, start, chunk, sessionId) => {
    const formData = new FormData();
    formData.append("file", chunk);
    formData.append("sessionId", sessionId);
    formData.append("start", start);
    formData.append("total_size", file.size);
    formData.append("filename", file.name);

    console.log(`Uploading chunk for ${file.name}: ${start}-${start + chunk.size} of ${file.size}`);

    const response = await fetch("http://localhost:5000/upload-chunk", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error(`Upload chunk failed: ${response.statusText}`);
    }
    const result = await response.json();
    console.log(`Chunk upload response:`, result);

    // Update progress for this specific file
    const progress = Math.min(Math.round((start + chunk.size) / file.size * 100), 100);
    setFileProgress((prevProgress) => ({
      ...prevProgress,
      [file.name]: progress, // Update progress for this file
    }));

    return result;
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      console.warn("No files selected for upload");
      alert("Tidak ada file yang dipilih.");
      return;
    }

    setIsUploading(true);
    const sessionId = Date.now().toString();
    console.log(`Starting upload session: ${sessionId}`);

    try {
      for (const file of selectedFiles) {
        console.log(`Processing file: ${file.name} (${file.size} bytes)`);
        let start = 0;
        while (start < file.size) {
          const chunk = file.slice(start, start + CHUNK_SIZE);
          await uploadChunk(file, start, chunk, sessionId);
          start += CHUNK_SIZE;
        }
        // Once the file is uploaded, update progress to 100%
        setFileProgress((prevProgress) => ({
          ...prevProgress,
          [file.name]: 100,
        }));
        console.log(`Completed uploading file: ${file.name}`);
      }

      console.log("All files uploaded, finalizing...");
      const finalizeResponse = await fetch("http://localhost:5000/finalize-upload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          files: selectedFiles.map(f => ({
            filename: f.name,
            size: f.size,
          })),
        }),
      });

      if (!finalizeResponse.ok) {
        throw new Error("Failed to finalize upload");
      }

      const result = await finalizeResponse.json();
      console.log("Upload finalized:", result);

      setUploadStatus({
        uploaded: true,
        analyzed: true,  // Setelah di-analyze
        message: result.message,
        analysisResults: result.sensitive_data_files || [], // Menyimpan hasil analisis
      });

      alert("Upload berhasil!");
      setIsAnalyzing(true); // Start analyzing after upload completes
      
      // After upload completes, analyze files with Gemini API
      await analyzeFilesWithGemini(result.saved_files);
      
    } catch (error) {
      console.error("Upload error:", error);
      setUploadStatus({
        uploaded: false,
        analyzed: false,
        message: error.message,
      });
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
      setIsAnalyzing(false);
    }
  };

  const analyzeFilesWithGemini = async (filePaths) => {
    try {
      setIsAnalyzing(true);
      console.log("Starting file analysis with Gemini API");
      
      const analysisResponse = await fetch("http://localhost:5000/analyze-files", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          filePaths: filePaths,
        }),
      });
      
      if (!analysisResponse.ok) {
        throw new Error("Failed to analyze files");
      }
      
      const result = await analysisResponse.json();
      console.log("Analysis results:", result);
      
      // Update analysis details with results
      setAnalysisDetails(result.analysisDetails || {});
      
      setUploadStatus(prevState => ({
        ...prevState,
        analyzed: true,
        analysisResults: result.sensitiveFlaggedFiles || [],
      }));
      
    } catch (error) {
      console.error("Analysis error:", error);
      alert("Terjadi kesalahan saat menganalisis file.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  const downloadAnalysis = (filename) => {
    // Fungsi untuk mendownload file hasil analisis
    const url = `http://localhost:5000/download-analysis?filename=${filename}`;
    window.open(url, "_blank"); // Membuka file di tab baru untuk di-download
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`} >
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`} >
              Upload Folder
              <input type="file" webkitdirectory="" directory="" multiple onChange={handleFolderUpload} disabled={fileUploaded} />
            </label>
          </div>
        </div>
        <button className="upload-button" onClick={handleUploadToBackend}>
          {isUploading ? "Uploading..." : isAnalyzing ? "Analyzing..." : "Upload"}
        </button>
      </div>

      {selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul>
            {selectedFiles.map((file, index) => (
              <li key={index}>
                {file.name}
                {fileProgress[file.name] !== undefined && (
                  <span style={{ marginLeft: "10px", fontSize: "12px" }}>
                    {fileProgress[file.name]}%
                  </span>
                )}
                {/* Tombol Download Hasil Analisis */}
                {uploadStatus.analyzed && uploadStatus.analysisResults.includes(file.name) && (
                  <button onClick={() => downloadAnalysis(file.name)} style={{ marginLeft: "10px" }}>
                    Download Hasil Analisis
                  </button>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
      
      {/* Container hasil analisis */}
      {uploadStatus.analyzed && (
        <div className="analysis-results-container">
          <h3>Hasil Analisis HauntAI</h3>
          {uploadStatus.analysisResults.length > 0 ? (
            <div className="sensitive-files-list">
              <h4>File dengan Data Sensitif Terdeteksi:</h4>
              <ul>
                {uploadStatus.analysisResults.map((filename, index) => (
                  <li key={index} className="sensitive-file-item">
                    <div className="file-name">{filename}</div>
                    
                    {analysisDetails[filename] && (
                      <div className="analysis-detail">
                        <h5>Alasan Terdeteksi Sensitif:</h5>
                        <p>{analysisDetails[filename].reason}</p>
                        
                        <h5>Data Sensitif yang Ditemukan:</h5>
                        <ul className="sensitive-data-list">
                          {analysisDetails[filename].items.map((item, idx) => (
                            <li key={idx}>
                              <strong>{item.type}</strong>: {item.content}
                              <p className="risk-level">
                                Risk Level: <span className={`risk-${item.risk.toLowerCase()}`}>{item.risk}</span>
                              </p>
                            </li>
                          ))}
                        </ul>
                        
                        <h5>Rekomendasi:</h5>
                        <p>{analysisDetails[filename].recommendation}</p>
                      </div>
                    )}
                    
                    <button 
                      onClick={() => downloadAnalysis(filename)} 
                      className="download-analysis-btn"
                    >
                      Download Laporan Detail
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ) : (
            <div className="no-sensitive-data">
              <p>Tidak terdeteksi data sensitif pada file yang diupload.</p>
              <p>File anda aman untuk dibagikan.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
