import React, { useState } from 'react';
import './Homepage.css';

export default function Homepage() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [uploadMessage, setUploadMessage] = useState(null);

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedItems(files);
  };

  const handleUpload = async () => {
    if (selectedItems.length === 0) {
      alert('Please select a file or folder.');
      return;
    }

    const formData = new FormData();
    selectedItems.forEach((file) => {
      formData.append('files', file);
    });

    try {
      const response = await fetch('http://127.0.0.1:5001/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        setUploadMessage('File(s) or folder uploaded successfully!');
      } else {
        setUploadMessage('Error uploading file(s).');
      }
    } catch (err) {
      console.error('Error uploading file(s):', err);
      setUploadMessage('Error uploading file(s).');
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <h1 className="title">Upload Source Code</h1>
            <p className="description">You can upload files or folders.</p>
            <input
              type="file"
              onChange={handleFileChange}
              className="file-input"
              multiple
              webkitdirectory=""
            />
            <button onClick={handleUpload} className="upload-button">
              Upload
            </button>
            {uploadMessage && (
              <div className="upload-message">
                <h2>{uploadMessage}</h2>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
