import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [totalSizeMB, setTotalSizeMB] = useState(0);
  const [uploadStatus, setUploadStatus] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const MAX_SIZE_MB = 1024; // 1GB

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      let totalSize = 0;
      const fileList = Array.from(files);

      fileList.forEach(file => {
        totalSize += file.size;
      });

      totalSize = totalSize / (1024 * 1024); // Convert ke MB

      if (totalSize > MAX_SIZE_MB) {
        setUploadStatus({
          error: `Total ukuran file terlalu besar! Maksimum 1GB.`,
          total_size_MB: totalSize.toFixed(2),
          total_size_GB: (totalSize / 1024).toFixed(3)
        });
        return;
      }

      setSelectedFiles(fileList);
      setTotalSizeMB(totalSize);
      setFileUploaded(true);
      setFolderUploaded(false);
      setUploadStatus(null);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let fileList = [];
    let totalSize = 0;

    for (let i = 0; i < files.length; i++) {
      fileList.push(files[i]);
      totalSize += files[i].size;
    }

    totalSize = totalSize / (1024 * 1024); // Convert ke MB

    if (totalSize > MAX_SIZE_MB) {
      setUploadStatus({
        error: `Total ukuran file terlalu besar! Maksimum 1GB.`,
        total_size_MB: totalSize.toFixed(2),
        total_size_GB: (totalSize / 1024).toFixed(3)
      });
      return;
    }

    if (fileList.length > 0) {
      setSelectedFiles(fileList);
      setTotalSizeMB(totalSize);
      setFolderUploaded(true);
      setFileUploaded(false);
      setFolderContents(fileList.map(file => file.name));
      setUploadStatus(null);
    } else {
      alert("Tidak ada file yang ditemukan.");
      setFolderUploaded(false);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input type="file" webkitdirectory="" directory="" multiple onChange={handleFolderUpload} disabled={fileUploaded} />
            </label>
          </div>
        </div>
        <button className="upload-button" disabled>
          Upload
        </button>
      </div>

      {selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <p><strong>Total Ukuran:</strong> {totalSizeMB.toFixed(2)} MB ({(totalSizeMB / 1024).toFixed(3)} GB)</p>
          <ul>
            {selectedFiles.slice(0, 10).map((file, index) => (
              <li key={index}>{file.name}</li>
            ))}
            {selectedFiles.length > 10 && <li>... ({selectedFiles.length - 10} file lainnya)</li>}
          </ul>
        </div>
      )}

      {uploadStatus?.error && (
        <div className="upload-status error">
          <h3>Kesalahan:</h3>
          <p>{uploadStatus.error}</p>
          <p><strong>Total Ukuran File:</strong> {uploadStatus.total_size_MB} MB ({uploadStatus.total_size_GB} GB)</p>
        </div>
      )}
    </div>
  );
}
