import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderContents, setFolderContents] = useState([]); // Menyimpan daftar file dalam folder
  const [uploadResponse, setUploadResponse] = useState(null);

  const handleUpload = async (files) => {
    const formData = new FormData();
    for (const file of files) {
      formData.append("files", file);
    }

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      setUploadResponse(result);

      if (result.accepted_files.length > 0) {
        setFolderContents(result.accepted_files);
        setFolderUploaded(true);
        setFileUploaded(false);
      } else {
        setFolderUploaded(false);
      }
    } catch (error) {
      console.error("Upload failed:", error);
    }
  };

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      handleUpload(files);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input type="file" onChange={handleFileUpload} disabled={folderUploaded} multiple />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input type="file" webkitdirectory="" directory="" onChange={handleFolderUpload} disabled={fileUploaded} multiple />
            </label>
          </div>
        </div>
      </div>

      {/* Container Baru untuk Menampilkan Isi Folder */}
      {folderUploaded && (
        <div className="folder-content-container">
          <h3>File:</h3>
          <ul>
            {folderContents.map((file, index) => (
              <li key={index}>{file}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
