import React, { useState } from 'react';
import axios from 'axios';

export default function UploadPage() {
    const [files, setFiles] = useState([]);
    const [message, setMessage] = useState('');

    // Maksimum ukuran file 1GB
    const MAX_FILE_SIZE = 1 * 1024 * 1024 * 1024; // 1GB dalam byte

    // Fungsi untuk memeriksa ekstensi file yang diterima
    const allowedExtensions = ['js', 'python'];

    const handleFileChange = (e) => {
        const selectedFiles = e.target.files;
        const validFiles = [];
        const invalidFiles = [];
        
        for (let i = 0; i < selectedFiles.length; i++) {
            const file = selectedFiles[i];
            const fileExtension = file.name.split('.').pop().toLowerCase();
            const fileSize = file.size;

            // Memeriksa apakah ekstensi file valid dan ukuran file tidak melebihi 1GB
            if (allowedExtensions.includes(fileExtension) && fileSize <= MAX_FILE_SIZE) {
                validFiles.push(file);
            } else {
                invalidFiles.push(file);
            }
        }

        if (validFiles.length > 0) {
            setFiles(validFiles);
            setMessage(`${validFiles.length} valid files selected.`);
        } else {
            setMessage('No valid files selected. Please select only .js or .python files under 1GB.');
        }

        if (invalidFiles.length > 0) {
            const invalidFileNames = invalidFiles.map(file => file.name).join(', ');
            setMessage(prevMessage => `${prevMessage} Invalid files: ${invalidFileNames}.`);
        }
    };

    const handleUpload = async () => {
        if (files.length === 0) {
            alert('Please select valid files to upload.');
            return;
        }

        const formData = new FormData();
        files.forEach(file => {
            formData.append('file', file);
        });

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            console.log('Files uploaded:', response.data);
            alert(response.data.message);
        } catch (error) {
            console.error('Error uploading files:', error);
            alert("File upload failed!");
        }
    };

    return (
        <div>
            <h1>Upload Files (Max 1GB, .js or .python only)</h1>
            <input 
                type="file" 
                multiple 
                onChange={handleFileChange} 
                webkitdirectory 
                directory 
                style={{ marginBottom: '20px' }}
            />
            <div>
                <p>{message}</p>
            </div>
            <button onClick={handleUpload}>Upload Files</button>

            {files.length > 0 && (
                <div>
                    <h3>Files to Upload:</h3>
                    <ul>
                        {files.map((file, index) => (
                            <li key={index}>{file.name} ({(file.size / (1024 * 1024)).toFixed(2)} MB)</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}
