import React, { useState } from 'react';
import './Homepage.css';

function Homepage() {
  const [selectedFiles, setSelectedFiles] = useState(null);
  const [filteredFiles, setFilteredFiles] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFiles(e.target.files);
  };

  const handleUpload = async () => {
    if (!selectedFiles) {
      alert('No files selected!');
      return;
    }

    const formData = new FormData();
    Array.from(selectedFiles).forEach((file) => {
      formData.append('files', file);
    });

    try {
      const response = await fetch('http://127.0.0.1:5050/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const result = await response.json();
        setFilteredFiles(result);
      } else {
        alert('Error uploading files');
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="homepage">
      <h1>Upload Files or Folder</h1>
      <input type="file" webkitdirectory="" multiple onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      {filteredFiles && (
        <div className="result">
          <h2>Filtered Files</h2>
          <div className="container">
            <div>
              <h3>JavaScript Files</h3>
              <ul>
                {filteredFiles.jsFiles.map((file, index) => (
                  <li key={index}>{file}</li>
                ))}
              </ul>
            </div>
            <div>
              <h3>Python Files</h3>
              <ul>
                {filteredFiles.pyFiles.map((file, index) => (
                  <li key={index}>{file}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Homepage;
