import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    addFiles(files);
  };

  const handleFolderChange = (e) => {
    const files = Array.from(e.target.files);
    addFiles(files);
  };

  const addFiles = (files) => {
    const totalSize = files.reduce((sum, file) => sum + file.size, 0) + selectedItems.reduce((sum, file) => sum + file.size, 0);

    if (totalSize > 1024 * 1024 * 1024) { // Batas total 1GB
      setError("Selected files exceed 1GB. Please upload smaller items.");
    } else {
      setError("");
      setSelectedItems((prev) => [...prev, ...files]);
    }
  };

  const handleUpload = () => {
    if (selectedItems.length > 0) {
      console.log("Uploading:");
      selectedItems.forEach((file) => console.log("File:", file.name));
      alert("Upload successful!");
    } else {
      alert("Please select files or a folder first.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <div className="text-center mb-6">
              <h1 className="title">Upload Your File or Folder</h1>
              <p className="description">Maximum size: 1GB</p>
            </div>
            <div className="form-container">
              <label className="upload-label">
                Upload
                <input
                  type="file"
                  onChange={handleFileChange}
                  className="file-input"
                />
                <input
                  type="file"
                  onChange={handleFolderChange}
                  className="file-input"
                  webkitdirectory="true"
                />
              </label>
              {error && <p className="error-message">{error}</p>}
              <button
                onClick={handleUpload}
                disabled={selectedItems.length === 0}
                className="upload-button"
              >
                Confirm Upload
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
