import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderStructure, setFolderStructure] = useState([]); // Store full folder structure
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState({
    uploaded: false,
    analyzed: false,
    message: "",
    analysisResults: [],
  });
  const [isUploading, setIsUploading] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [fileProgress, setFileProgress] = useState({});
  const [analysisProgress, setAnalysisProgress] = useState({});

  const CHUNK_SIZE = 1024 * 1024 * 500; // 500MB

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
      setFileUploaded(true);
      setFolderUploaded(false);
      setFolderStructure([]); // Clear folder structure
      console.log(`Selected ${files.length} files for upload`);
    }
  };

  // Process folder structure from DataTransferItemList
  const processEntries = async (entry, path = "") => {
    const items = [];
    
    if (entry.isFile) {
      return new Promise((resolve) => {
        entry.file(file => {
          // Create a new file object with the path information
          const fileWithPath = Object.assign(file, {
            webkitRelativePath: path + file.name,
            fullPath: path + file.name,
            isFile: true
          });
          items.push(fileWithPath);
          resolve(items);
        });
      });
    } else if (entry.isDirectory) {
      const dirReader = entry.createReader();
      const newPath = path + entry.name + "/";
      
      // Create a directory entry object
      const dirEntry = {
        name: entry.name,
        fullPath: newPath,
        isDirectory: true,
        children: []
      };
      
      items.push(dirEntry);
      
      return new Promise((resolve) => {
        const readEntries = () => {
          dirReader.readEntries(async (entries) => {
            if (entries.length === 0) {
              resolve(items);
            } else {
              for (const childEntry of entries) {
                const childItems = await processEntries(childEntry, newPath);
                
                // If this is a directory, add its children to the directory entry
                if (childEntry.isDirectory) {
                  dirEntry.children.push(...childItems);
                } else {
                  // If it's a file, add it to our items array
                  items.push(...childItems);
                }
              }
              readEntries(); // Continue reading until all entries are processed
            }
          });
        };
        readEntries();
      });
    }
    
    return items;
  };

  const handleFolderUpload = async (event) => {
    if (!event.target.files || event.target.files.length === 0) return;
    
    // Get the DataTransferItems from the input
    const items = event.dataTransfer ? event.dataTransfer.items : null;
    
    if (items && items.length > 0) {
      // Using the modern DataTransferItem API to get folder structure
      setIsUploading(true);
      const rootEntry = items[0].webkitGetAsEntry();
      
      if (rootEntry) {
        const structure = await processEntries(rootEntry);
        console.log("Folder structure:", structure);
        
        // Extract all files from the structure
        const allFiles = extractFilesFromStructure(structure);
        console.log("All files:", allFiles);
        
        setFolderStructure(structure);
        setSelectedFiles(allFiles);
        setFolderUploaded(true);
        setFileUploaded(false);
      }
      setIsUploading(false);
    } else {
      // Fallback for older browsers or when drag-and-drop is not used
      const files = Array.from(event.target.files);
      
      // Create folder structure from webkitRelativePath
      const structure = createFolderStructureFromFiles(files);
      console.log("Created folder structure:", structure);
      
      // Filter files to only include .js or .py files
      const fileList = files.filter(file => 
        file.name.endsWith(".js") || file.name.endsWith(".py")
      );
      
      if (fileList.length > 0) {
        setSelectedFiles(fileList);
        setFolderStructure(structure);
        setFolderUploaded(true);
        setFileUploaded(false);
        console.log(`Selected ${fileList.length} files from folder`);
      } else {
        console.warn("No .js or .py files found in folder");
        alert("Tidak ada file dengan ekstensi .js atau .py yang ditemukan.");
        setFolderUploaded(false);
      }
    }
  };

  // Helper function to create folder structure from file list (using webkitRelativePath)
  const createFolderStructureFromFiles = (files) => {
    const root = { name: "root", isDirectory: true, children: [] };
    
    for (const file of files) {
      if (!file.webkitRelativePath) continue;
      
      const pathParts = file.webkitRelativePath.split('/');
      let currentLevel = root;
      
      // Process directories in the path
      for (let i = 0; i < pathParts.length - 1; i++) {
        const folderName = pathParts[i];
        let folderFound = false;
        
        // Check if the folder already exists at this level
        for (const child of currentLevel.children) {
          if (child.isDirectory && child.name === folderName) {
            currentLevel = child;
            folderFound = true;
            break;
          }
        }
        
        // If folder doesn't exist, create it
        if (!folderFound) {
          const newFolder = {
            name: folderName,
            isDirectory: true,
            fullPath: pathParts.slice(0, i + 1).join('/') + '/',
            children: []
          };
          currentLevel.children.push(newFolder);
          currentLevel = newFolder;
        }
      }
      
      // Add the file to the current directory level
      currentLevel.children.push({
        name: file.name,
        fullPath: file.webkitRelativePath,
        isFile: true,
        file: file
      });
    }
    
    // Return children of root (skip the artificial root node)
    return root.children;
  };

  // Helper function to extract all file objects from structure
  const extractFilesFromStructure = (structure) => {
    const files = [];
    
    const traverse = (items) => {
      for (const item of items) {
        if (item.isFile) {
          files.push(item);
        } else if (item.isDirectory && item.children) {
          traverse(item.children);
        }
      }
    };
    
    traverse(structure);
    return files;
  };

  const uploadChunk = async (file, start, chunk, sessionId) => {
    const formData = new FormData();
    formData.append("file", chunk);
    formData.append("sessionId", sessionId);
    formData.append("start", start);
    formData.append("total_size", file.size);
    formData.append("filename", file.name);
    // Add path information for folder structure
    if (file.fullPath) {
      formData.append("filepath", file.fullPath);
    }

    console.log(`Uploading chunk for ${file.name}: ${start}-${start + chunk.size} of ${file.size}`);

    const response = await fetch("http://localhost:5000/upload-chunk", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error(`Upload chunk failed: ${response.statusText}`);
    }
    const result = await response.json();
    console.log(`Chunk upload response:`, result);

    // Update progress for this specific file
    const progress = Math.min(Math.round((start + chunk.size) / file.size * 100), 100);
    setFileProgress((prevProgress) => ({
      ...prevProgress,
      [file.name]: progress, // Update progress for this file
    }));

    return result;
  };

  // Function to analyze files with Gemini API
  const analyzeFilesWithGemini = async (savedFiles, uploadFolder) => {
    setIsAnalyzing(true);
    console.log("Starting Gemini analysis for files:", savedFiles);
    
    try {
      // Initialize analysis progress for each file
      const initialProgress = {};
      savedFiles.forEach(filePath => {
        const fileName = filePath.split('/').pop();
        initialProgress[fileName] = 0;
      });
      setAnalysisProgress(initialProgress);
      
      // Send request to analyze files
      const analysisResponse = await fetch("http://localhost:5000/analyze-files", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          files: savedFiles,
          folder: uploadFolder,
          analysisPrompt: "Analyze this code and identify any security vulnerabilities, hardcoded credentials, or sensitive data. Also suggest improvements for code quality and performance. Structure your response with clear sections for issues found and recommendations.",
        }),
      });

      if (!analysisResponse.ok) {
        throw new Error("Failed to analyze files with Gemini API");
      }

      const analysisResult = await analysisResponse.json();
      console.log("Analysis completed:", analysisResult);

      setUploadStatus(prev => ({
        ...prev,
        analyzed: true,
        analysisResults: analysisResult.analyzed_files || [],
      }));

      alert("Analysis complete! You can now view the results.");
    } catch (error) {
      console.error("Analysis error:", error);
      alert("Error analyzing files: " + error.message);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      console.warn("No files selected for upload");
      alert("Tidak ada file yang dipilih.");
      return;
    }

    setIsUploading(true);
    const sessionId = Date.now().toString();
    console.log(`Starting upload session: ${sessionId}`);

    try {
      for (const file of selectedFiles) {
        console.log(`Processing file: ${file.name} (${file.size} bytes)`);
        let start = 0;
        while (start < file.size) {
          const chunk = file.slice(start, start + CHUNK_SIZE);
          await uploadChunk(file, start, chunk, sessionId);
          start += CHUNK_SIZE;
        }
        // Once the file is uploaded, update progress to 100%
        setFileProgress((prevProgress) => ({
          ...prevProgress,
          [file.name]: 100,
        }));
        console.log(`Completed uploading file: ${file.name}`);
      }

      console.log("All files uploaded, finalizing...");
      const finalizeResponse = await fetch("http://localhost:5000/finalize-upload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          files: selectedFiles.map(f => ({
            filename: f.name,
            size: f.size,
            filepath: f.fullPath || f.webkitRelativePath || f.name,
          })),
        }),
      });

      if (!finalizeResponse.ok) throw new Error("Failed to finalize upload");

      const result = await finalizeResponse.json();
      console.log("Upload finalized:", result);

      setUploadStatus(prevStatus => ({
        ...prevStatus,
        uploaded: true,
        analyzed: false,
        message: result.message,
        analysisResults: [],
      }));
      
      setIsUploading(false);
      setIsAnalyzing(true);
      
      alert("Upload berhasil! Now starting analysis...");
      
      // Start analyzing files with Gemini after upload completes
      await analyzeFilesWithGemini(result.saved_files, result.folder);
      
    } catch (error) {
      console.error("Upload error:", error);
      setUploadStatus({
        uploaded: false,
        analyzed: false,
        message: error.message,
      });
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
    }
  };

  const downloadAnalysis = (filename) => {
    // Fungsi untuk mendownload file hasil analisis
    const url = `http://localhost:5000/download-analysis?filename=${filename}`;
    window.open(url, "_blank"); // Membuka file di tab baru untuk di-download
  };

  // Recursive component to render folder structure
  const FolderTree = ({ items }) => {
    return (
      <ul className="folder-tree">
        {items.map((item, index) => (
          <li key={index} className={item.isDirectory ? "folder-item" : "file-item"}>
            <div className="item-name">
              {item.isDirectory ? (
                <span className="folder-icon">üìÅ {item.name}</span>
              ) : (
                <span className="file-icon">üìÑ {item.name}</span>
              )}
              
              {isUploading && fileProgress[item.name] !== undefined && (
                <span className="progress-indicator">
                  Uploading: {fileProgress[item.name]}%
                </span>
              )}
              
              {isAnalyzing && analysisProgress[item.name] !== undefined && (
                <span className="progress-indicator">
                  Analyzing...
                </span>
              )}
              
              {uploadStatus.analyzed && uploadStatus.analysisResults.includes(item.name) && (
                <button onClick={() => downloadAnalysis(item.name)} className="download-button">
                  View Analysis
                </button>
              )}
            </div>
            
            {item.isDirectory && item.children && item.children.length > 0 && (
              <FolderTree items={item.children} />
            )}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`} >
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`} >
              Upload Folder
              <input 
                type="file" 
                webkitdirectory="" 
                directory="" 
                multiple 
                onChange={handleFolderUpload} 
                disabled={fileUploaded} 
              />
            </label>
          </div>
        </div>
        <button 
          className="upload-button" 
          onClick={handleUploadToBackend} 
          disabled={isUploading || isAnalyzing}
        >
          {isUploading ? "Uploading..." : isAnalyzing ? "Analyzing with AI..." : "Upload"}
        </button>
      </div>

      {/* Show folder structure when folder is uploaded */}
      {folderUploaded && folderStructure.length > 0 && (
        <div className="folder-content-container">
          <h3>Struktur Folder:</h3>
          <FolderTree items={folderStructure} />
        </div>
      )}

      {/* Show flat file list when individual files are uploaded */}
      {fileUploaded && selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul className="file-list">
            {selectedFiles.map((file, index) => (
              <li key={index} className="file-item">
                <span className="file-icon">üìÑ {file.name}</span>
                {isUploading && fileProgress[file.name] !== undefined && (
                  <span className="progress-indicator">
                    Uploading: {fileProgress[file.name]}%
                  </span>
                )}
                {isAnalyzing && analysisProgress[file.name] !== undefined && (
                  <span className="progress-indicator">
                    Analyzing...
                  </span>
                )}
                {uploadStatus.analyzed && uploadStatus.analysisResults.includes(file.name) && (
                  <button onClick={() => downloadAnalysis(file.name)} className="download-button">
                    View Analysis
                  </button>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
