import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.size > 1024 * 1024 * 1024) {
      alert("File terlalu besar! Maksimum 1GB.");
      return;
    }
    if (event.target.files.length > 0) {
      setFileUploaded(true);
      setFolderUploaded(false);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    let isTooLarge = false;
    for (let i = 0; i < files.length; i++) {
      if (files[i].size > 1024 * 1024 * 1024) {
        isTooLarge = true;
        break;
      }
    }
    if (isTooLarge) {
      alert("Salah satu file dalam folder terlalu besar! Maksimum 1GB per file.");
      return;
    }
    if (event.target.files.length > 0) {
      setFolderUploaded(true);
      setFileUploaded(false);
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`}>
              Upload File
              <input
                type="file"
                onChange={handleFileUpload}
                disabled={folderUploaded}
              />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`}>
              Upload Folder
              <input
                type="file"
                webkitdirectory=""
                directory=""
                onChange={handleFolderUpload}
                disabled={fileUploaded}
              />
            </label>
          </div>
        </div>
        <button className="upload-button">Upload</button>
      </div>
    </div>
  );
}
