import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedItems, setSelectedItems] = useState([]);
  const [error, setError] = useState("");

  const handleSelectionChange = (e) => {
    const files = Array.from(e.target.files);
    const totalSize = files.reduce((sum, file) => sum + file.size, 0);

    if (totalSize > 1024 * 1024 * 1024) { // Batas total 1GB
      setError("Selected files or folder exceed 1GB. Please upload smaller items.");
      setSelectedItems([]);
    } else {
      setError("");
      setSelectedItems(files);
    }
  };

  const handleFileChange = (e) => {
    setSelectedItems(Array.from(e.target.files)); // Handle multiple files
  };

  const handleUpload = async () => {
    if (selectedItems.length === 0) {
      alert("Please select a file or folder.");
      return;
    }

    console.log("Uploading:", selectedItems);
    selectedItems.forEach((file) => console.log("File:", file.name));
    alert("Upload successful!");
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <div className="navbar-brand">HauntAI</div>
      </nav>
      <div className="container">
        <div className="card-container">
          <div className="card">
            <div className="text-center mb-6">
              <h1 className="title">Upload Your File or Folder</h1>
              <p className="description">Maximum size: 1GB</p>
            </div>
            <div className="form-container">
              {/* Input file yang mendukung file dan folder */}
              <input
                type="file"
                onChange={handleSelectionChange}
                className="file-input"
                multiple
                webkitdirectory=""
              />
              {error && <p className="error-message">{error}</p>}
              <button
                onClick={handleUpload}
                disabled={selectedItems.length === 0}
                className="upload-button"
              >
                Upload
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
