import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);

  const handleFileUpload = (event) => {
    if (event.target.files.length > 0) {
      setFileUploaded(true);
      setFolderUploaded(false);
    }
  };

  const handleFolderUpload = (event) => {
    if (event.target.files.length > 0) {
      setFolderUploaded(true);
      setFileUploaded(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-cover bg-center bg-no-repeat" style={{ backgroundImage: "url('../../assets/walpaper.jpg')" }}>
      <nav className="w-full bg-black bg-opacity-80 text-white text-center py-4 text-2xl font-bold shadow-lg">HauntAI</nav>
      <div className="bg-white bg-opacity-90 p-6 rounded-lg shadow-lg flex flex-col items-center w-96 mt-10">
        <h2 className="text-lg font-semibold mb-4">Upload File atau Folder</h2>
        <div className="flex gap-4 mb-4">
          <label className={`px-4 py-2 border rounded-lg cursor-pointer text-center ${folderUploaded ? "opacity-50 cursor-not-allowed" : "hover:bg-gray-200"}`}>
            <input type="file" onChange={handleFileUpload} disabled={folderUploaded} className="hidden" />
            Upload File
          </label>
          <label className={`px-4 py-2 border rounded-lg cursor-pointer text-center ${fileUploaded ? "opacity-50 cursor-not-allowed" : "hover:bg-gray-200"}`}>
            <input type="file" webkitdirectory="" directory="" onChange={handleFolderUpload} disabled={fileUploaded} className="hidden" />
            Upload Folder
          </label>
        </div>
        <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition">Upload</button>
      </div>
    </div>
  );
}
