import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [uploadedItem, setUploadedItem] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleUpload = (event) => {
    const fileOrFolder = event.target.files[0];

    if (fileOrFolder) {
      const maxSize = 1 * 1024 * 1024 * 1024; // 1GB
      if (fileOrFolder.size > maxSize) {
        setErrorMessage("File size exceeds 1GB. Please upload a smaller file.");
        setUploadedItem(null);
      } else {
        setUploadedItem(fileOrFolder);
        setErrorMessage(null);
      }
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>

      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <div className="upload-section">
          <label className="upload-label" htmlFor="file-upload">
            Choose File or Folder:
          </label>
          <input
            id="file-upload"
            type="file"
            webkitdirectory="true"
            onChange={handleUpload}
            className="upload-input"
          />
        </div>

        {errorMessage && (
          <div className="error-message">
            <p>{errorMessage}</p>
          </div>
        )}

        {uploadedItem && (
          <div className="uploaded-item">
            <h3>Uploaded Item:</h3>
            <p>{uploadedItem.name}</p>
          </div>
        )}
      </div>
    </div>
  );
}
