import React, { useState } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [selectedFiles, setSelectedFiles] = useState([]); // Untuk menyimpan file yang dipilih dari folder

  const handleFolderChange = (e) => {
    const files = Array.from(e.target.files); // Convert FileList to Array
    setSelectedFiles(files);
  };

  const handleFileSelection = (file) => {
    // Toggle selection of file
    setSelectedFiles((prevFiles) =>
      prevFiles.includes(file)
        ? prevFiles.filter((f) => f !== file)
        : [...prevFiles, file]
    );
  };

  const handleUpload = async () => {
    if (selectedFiles.length === 0) {
      alert("Please select at least one file to upload.");
      return;
    }

    const formData = new FormData();
    selectedFiles.forEach((file) => formData.append("files", file));

    try {
      const response = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      alert(data.message);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files.");
    }
  };

  return (
    <div className="container-homepage">
      <nav className="navbar">
        <h1>HauntAI</h1>
      </nav>
      <div className="upload-container">
        <h3>Upload Folder</h3>
        <input type="file" multiple webkitdirectory onChange={handleFolderChange} />
        <div className="file-list">
          {selectedFiles.map((file, index) => (
            <div key={index} className="file-item">
              <input
                type="checkbox"
                checked={selectedFiles.includes(file)}
                onChange={() => handleFileSelection(file)}
              />
              <span>{file.webkitRelativePath || file.name}</span>
            </div>
          ))}
        </div>
        <button onClick={handleUpload}>Upload Selected Files</button>
      </div>
    </div>
  );
}
