import React, { useState, useEffect } from "react";
import "./Homepage.css";

export default function HomePage() {
  const [fileUploaded, setFileUploaded] = useState(false);
  const [folderUploaded, setFolderUploaded] = useState(false);
  const [folderStructure, setFolderStructure] = useState({});
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState({
    uploaded: false,
    analyzed: false,
    message: "",
    analysisResults: [],
  });
  const [isUploading, setIsUploading] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [fileProgress, setFileProgress] = useState({});
  const [analysisProgress, setAnalysisProgress] = useState({});

  const CHUNK_SIZE = 1024 * 1024 * 500; // 500MB

  const handleFileUpload = (event) => {
    const files = event.target.files;
    if (files.length > 0) {
      setSelectedFiles(Array.from(files));
      setFileUploaded(true);
      setFolderUploaded(false);
      setFolderStructure({});
      console.log(`Selected ${files.length} files for upload`);
    }
  };

  const handleFolderUpload = (event) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;
    
    // Convert FileList to Array
    const fileArray = Array.from(files);
    
    // Build folder structure
    const structure = {};
    
    fileArray.forEach(file => {
      // Get the relative path (folder structure)
      const path = file.webkitRelativePath || "";
      if (!path) return;
      
      // Split path into parts
      const parts = path.split('/');
      
      // Start with the root of our structure
      let current = structure;
      
      // Navigate through the path parts (except the filename at the end)
      for (let i = 0; i < parts.length - 1; i++) {
        const part = parts[i];
        // Create folder if it doesn't exist
        if (!current[part]) {
          current[part] = { 
            __isFolder: true,
            __name: part,
            __path: parts.slice(0, i + 1).join('/')
          };
        }
        // Move to this folder
        current = current[part];
      }
      
      // Add the file to the current folder
      const fileName = parts[parts.length - 1];
      current[fileName] = {
        __isFile: true,
        __name: fileName,
        __path: path,
        __fileObj: file,
        __size: file.size
      };
    });
    
    console.log("Built folder structure:", structure);
    
    // Filter to get only .js and .py files
    const jsAndPyFiles = fileArray.filter(file => 
      file.name.endsWith(".js") || file.name.endsWith(".py")
    );
    
    if (jsAndPyFiles.length > 0) {
      setSelectedFiles(jsAndPyFiles);
      setFolderStructure(structure);
      setFolderUploaded(true);
      setFileUploaded(false);
      console.log(`Selected ${jsAndPyFiles.length} files from folder`);
    } else {
      console.warn("No .js or .py files found in folder");
      alert("Tidak ada file dengan ekstensi .js atau .py yang ditemukan.");
      setFolderUploaded(false);
    }
  };

  const uploadChunk = async (file, start, chunk, sessionId) => {
    const formData = new FormData();
    formData.append("file", chunk);
    formData.append("sessionId", sessionId);
    formData.append("start", start);
    formData.append("total_size", file.size);
    formData.append("filename", file.name);
    
    // Add the relative path for folder structure
    if (file.webkitRelativePath) {
      formData.append("filepath", file.webkitRelativePath);
    }

    console.log(`Uploading chunk for ${file.name}: ${start}-${start + chunk.size} of ${file.size}`);

    const response = await fetch("http://localhost:5000/upload-chunk", {
      method: "POST",
      body: formData,
    });

    if (!response.ok) {
      throw new Error(`Upload chunk failed: ${response.statusText}`);
    }
    const result = await response.json();
    console.log(`Chunk upload response:`, result);

    // Update progress for this specific file
    const progress = Math.min(Math.round((start + chunk.size) / file.size * 100), 100);
    setFileProgress((prevProgress) => ({
      ...prevProgress,
      [file.name]: progress, // Update progress for this file
    }));

    return result;
  };

  // Function to analyze files with Gemini API
  const analyzeFilesWithGemini = async (savedFiles, uploadFolder) => {
    setIsAnalyzing(true);
    console.log("Starting Gemini analysis for files:", savedFiles);
    
    try {
      // Initialize analysis progress for each file
      const initialProgress = {};
      savedFiles.forEach(filePath => {
        const fileName = filePath.split('/').pop();
        initialProgress[fileName] = 0;
      });
      setAnalysisProgress(initialProgress);
      
      // Send request to analyze files
      const analysisResponse = await fetch("http://localhost:5000/analyze-files", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          files: savedFiles,
          folder: uploadFolder,
          analysisPrompt: "Analyze this code and identify any security vulnerabilities, hardcoded credentials, or sensitive data. Also suggest improvements for code quality and performance. Structure your response with clear sections for issues found and recommendations.",
        }),
      });

      if (!analysisResponse.ok) {
        throw new Error("Failed to analyze files with Gemini API");
      }

      const analysisResult = await analysisResponse.json();
      console.log("Analysis completed:", analysisResult);

      setUploadStatus(prev => ({
        ...prev,
        analyzed: true,
        analysisResults: analysisResult.analyzed_files || [],
      }));

      alert("Analysis complete! You can now view the results.");
    } catch (error) {
      console.error("Analysis error:", error);
      alert("Error analyzing files: " + error.message);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleUploadToBackend = async () => {
    if (selectedFiles.length === 0) {
      console.warn("No files selected for upload");
      alert("Tidak ada file yang dipilih.");
      return;
    }

    setIsUploading(true);
    const sessionId = Date.now().toString();
    console.log(`Starting upload session: ${sessionId}`);

    try {
      for (const file of selectedFiles) {
        console.log(`Processing file: ${file.name} (${file.size} bytes)`);
        let start = 0;
        while (start < file.size) {
          const chunk = file.slice(start, start + CHUNK_SIZE);
          await uploadChunk(file, start, chunk, sessionId);
          start += CHUNK_SIZE;
        }
        // Once the file is uploaded, update progress to 100%
        setFileProgress((prevProgress) => ({
          ...prevProgress,
          [file.name]: 100,
        }));
        console.log(`Completed uploading file: ${file.name}`);
      }

      console.log("All files uploaded, finalizing...");
      const finalizeResponse = await fetch("http://localhost:5000/finalize-upload", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          sessionId,
          files: selectedFiles.map(f => ({
            filename: f.name,
            size: f.size,
            filepath: f.webkitRelativePath || f.name,
          })),
        }),
      });

      if (!finalizeResponse.ok) throw new Error("Failed to finalize upload");

      const result = await finalizeResponse.json();
      console.log("Upload finalized:", result);

      setUploadStatus(prevStatus => ({
        ...prevStatus,
        uploaded: true,
        analyzed: false,
        message: result.message,
        analysisResults: [],
      }));
      
      setIsUploading(false);
      setIsAnalyzing(true);
      
      alert("Upload berhasil! Now starting analysis...");
      
      // Start analyzing files with Gemini after upload completes
      await analyzeFilesWithGemini(result.saved_files, result.folder);
      
    } catch (error) {
      console.error("Upload error:", error);
      setUploadStatus({
        uploaded: false,
        analyzed: false,
        message: error.message,
      });
      alert("Terjadi kesalahan saat mengupload file.");
    } finally {
      setIsUploading(false);
    }
  };

  const downloadAnalysis = (filename) => {
    // Fungsi untuk mendownload file hasil analisis
    const url = `http://localhost:5000/download-analysis?filename=${filename}`;
    window.open(url, "_blank"); // Membuka file di tab baru untuk di-download
  };

  // Recursive component to render folder structure
  const renderFolderStructure = (structure, level = 0, parentPath = "") => {
    if (!structure || Object.keys(structure).length === 0) return null;
    
    return (
      <ul className="folder-tree" style={{ paddingLeft: level > 0 ? "20px" : "0" }}>
        {Object.entries(structure).map(([key, value]) => {
          // Skip special properties
          if (key.startsWith("__")) return null;
          
          const isFolder = value.__isFolder;
          const name = value.__name || key;
          const path = value.__path || (parentPath ? `${parentPath}/${key}` : key);
          
          return (
            <li key={path} className={isFolder ? "folder-item" : "file-item"}>
              <div className="item-name" style={{ 
                display: "flex", 
                alignItems: "center", 
                padding: "5px",
                backgroundColor: isFolder ? "#f0f0f0" : "transparent",
                borderRadius: "4px",
                marginBottom: "3px"
              }}>
                <span style={{ marginRight: "5px" }}>
                  {isFolder ? "üìÅ" : "üìÑ"}
                </span>
                <span>{name}</span>
                
                {!isFolder && fileProgress[name] !== undefined && (
                  <span style={{ marginLeft: "10px", fontSize: "12px", color: "#666" }}>
                    {isUploading ? `Uploading: ${fileProgress[name]}%` : ""}
                    {isAnalyzing ? "Analyzing..." : ""}
                  </span>
                )}
                
                {!isFolder && uploadStatus.analyzed && uploadStatus.analysisResults.includes(name) && (
                  <button 
                    onClick={() => downloadAnalysis(name)} 
                    style={{
                      marginLeft: "10px",
                      padding: "2px 8px",
                      backgroundColor: "#4285f4",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer"
                    }}
                  >
                    View Analysis
                  </button>
                )}
              </div>
              
              {isFolder && renderFolderStructure(value, level + 1, path)}
            </li>
          );
        })}
      </ul>
    );
  };

  // Helper function to count files and folders
  const countContents = (structure) => {
    let files = 0;
    let folders = 0;
    
    const traverse = (obj) => {
      if (!obj) return;
      
      Object.values(obj).forEach(value => {
        if (value && typeof value === 'object') {
          if (value.__isFolder) {
            folders++;
            traverse(value);
          } else if (value.__isFile) {
            files++;
          }
        }
      });
    };
    
    traverse(structure);
    return { files, folders };
  };

  // Get folder statistics
  const folderStats = countContents(folderStructure);

  return (
    <div className="container-homepage">
      <nav className="navbar">HauntAI</nav>
      <div className="upload-container">
        <h2>Upload File or Folder</h2>
        <p className="upload-limit">Maksimum ukuran file: 1GB</p>
        <div className="upload-options">
          <div className="upload-box">
            <label className={`upload-label ${folderUploaded ? "disabled" : ""}`} >
              Upload File
              <input type="file" multiple onChange={handleFileUpload} disabled={folderUploaded} />
            </label>
          </div>
          <div className="upload-box">
            <label className={`upload-label ${fileUploaded ? "disabled" : ""}`} >
              Upload Folder
              <input 
                type="file" 
                webkitdirectory="" 
                directory="" 
                multiple 
                onChange={handleFolderUpload} 
                disabled={fileUploaded} 
              />
            </label>
          </div>
        </div>
        <button 
          className="upload-button" 
          onClick={handleUploadToBackend} 
          disabled={isUploading || isAnalyzing}
        >
          {isUploading ? "Uploading..." : isAnalyzing ? "Analyzing with AI..." : "Upload"}
        </button>
      </div>

      {/* Show folder structure when folder is uploaded */}
      {folderUploaded && Object.keys(folderStructure).length > 0 && (
        <div className="folder-content-container">
          <h3>Struktur Folder:</h3>
          <p style={{ marginBottom: "10px", fontSize: "14px", color: "#666" }}>
            {folderStats.folders} folders, {folderStats.files} files
          </p>
          {renderFolderStructure(folderStructure)}
        </div>
      )}

      {/* Show flat file list when individual files are uploaded */}
      {fileUploaded && selectedFiles.length > 0 && (
        <div className="folder-content-container">
          <h3>File yang Akan Diupload:</h3>
          <ul className="file-list">
            {selectedFiles.map((file, index) => (
              <li key={index} style={{ marginBottom: "8px", display: "flex", alignItems: "center" }}>
                <span style={{ marginRight: "8px" }}>üìÑ {file.name}</span>
                {isUploading && fileProgress[file.name] !== undefined && (
                  <span style={{ marginLeft: "10px", fontSize: "12px" }}>
                    Uploading: {fileProgress[file.name]}%
                  </span>
                )}
                {isAnalyzing && analysisProgress[file.name] !== undefined && (
                  <span style={{ marginLeft: "10px", fontSize: "12px" }}>
                    Analyzing...
                  </span>
                )}
                {uploadStatus.analyzed && uploadStatus.analysisResults.includes(file.name) && (
                  <button onClick={() => downloadAnalysis(file.name)} style={{
                    marginLeft: "10px",
                    padding: "2px 8px",
                    backgroundColor: "#4285f4",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer"
                  }}>
                    View Analysis
                  </button>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
